    //             })

    //             .catch(next);
  }
  // [GET] /me/trash/khoa
  trashKhoa(req, res, next) {
    // res.json(req.body)

    Khoa.findDeleted({})
      .then((khoa) => {
        res.render('me/trash-khoa', { khoa: mutipleMongooseToObject(khoa) });
      })

      .catch(next);
  }
}

module.exports = new MeMeController();

