"use strict";

var mongoose = require('mongoose');
var slug = require('mongoose-slug-updater');
var mongooseDelete = require('mongoose-delete');
var Schema = mongoose.Schema;
var Events = new Schema({
  name: {
    type: String,
    required: true
  },
  overview: {
    type: String,
    "default": ''
  },
  image: {
    type: String,
    "default": ''
  },
  images: [{
    path: {
      type: String
    },
    approve: {
      type: Number,
      "default": 0
    },
    isNotified: {
      type: Boolean,
      "default": false
    }
  }],
  documents: [{
    path: {
      type: String
    },
    approve: {
      type: Number,
      "default": 0
    },
    isNotified: {
      type: Boolean,
      "default": false
    }
  }],
  aim: {
    type: String
  },
  slug: {
    type: String,
    slug: 'name',
    unique: true
  },
  url: {
    type: String,
    "default": ''
  },
  activities: {
    type: String,
    "default": ''
  },
  outcomes: {
    type: String,
    "default": ''
  },
  dateup: {
    type: Date,
    "default": Date.now
