sourcesContent:
const Khoa = require('../models/Khoa');
const Project = require('../models/Project');
const { mutipleMongooseToObject } = require('../../util/mongoose');

class SiteController {
  //GET /home
  index(req, res, next) {
    Khoa.find({})
      .then((khoa) => {
        res.render('home', { khoa: mutipleMongooseToObject(khoa) });
      })

      .catch(next);
  }
  //GET /search
  search(req, res) {
    res.render('search');
  }
  create(req, res, next) {
    res.render('admin/project/create');
  }
  // [POST]/khoa/store
  store(req, res, next) {
    try {
      const imagePath = req.file ? `/img/${req.file.filename}` : '';

      const projectData = {
        name: req.body.name,
        overview: req.body.overview,
        aim: req.body.aim,
        activities: req.body.activities,
        outcomes: req.body.outcomes,

        timeline: {
          startDate: req.body.startDate || null,
          endDate: req.body.endDate || null,
        },
        // L∆∞u ƒë·ªëi t√°c v√†o DB
        image: imagePath,
      };

      const project = new Project(projectData);
      project
        .save()
        .then(() => res.redirect('/project/view'))
        .catch((error) => {
          console.error('L·ªói khi l∆∞u Project:', error);
          res.status(500).json({ message: 'L·ªói khi l∆∞u Project', error });
        });
    } catch (error) {
      next(error);
    }
  }

  view(req, res, next) {
    Project.find({})
      .then((project) => {
        res.render('admin/project/view', {
          project: mutipleMongooseToObject(project),
        });
      })

      .catch(next);
  }
  detail(req, res, next) {
    Project.findOne({ slug: req.params.slug })
      .lean()
      .then((project) => res.render('admin/project/detail', { project }))
      .catch(next);
  }
  edit(req, res, next) {
    Project.findById(req.params.id)
      .lean()
      .then((project) => res.render('admin/project/edit', { project }))
      .catch(next);
  }
  // [PUT]/khoa/:id
  update(req, res, next) {
    try {
      const updateData = {
        name: req.body.name,
        overview: req.body.overview,
        aim: req.body.aim,
        activities: req.body.activities,
        outcomes: req.body.outcomes,
      };

      // N·∫øu c√≥ ·∫£nh m·ªõi, c·∫≠p nh·∫≠t ·∫£nh, n·∫øu kh√¥ng gi·ªØ nguy√™n ·∫£nh c≈©
      if (req.file) {
        updateData.image = `/img/${req.file.filename}`;
      }

      // C·∫≠p nh·∫≠t th·ªùi gian n·∫øu c√≥ d·ªØ li·ªáu
      updateData.timeline = {};
      if (req.body.startDate)
        updateData.timeline.startDate = req.body.startDate;
      if (req.body.endDate) updateData.timeline.endDate = req.body.endDate;

      Project.updateOne({ _id: req.params.id }, updateData)
        .then(() => res.redirect('/project/view'))
        .catch((error) => {
          console.error('L·ªói khi c·∫≠p nh·∫≠t Project:', error);
          res.status(500).json({ message: 'L·ªói khi c·∫≠p nh·∫≠t Project', error });
        });
    } catch (error) {
      next(error);
    }
  }
}

module.exports = new SiteController();

