const Partner = require('../models/Partner');
const Project = require('../models/Project');
const { mutipleMongooseToObject } = require('../../util/mongoose');

class PartnerController {
  async create(req, res, next) {
    try {
      const projects = await Project.find({}).lean();
      res.render('admin/partner/create', { projects });
    } catch (error) {
      next(error);
    }
  }

  async store(req, res, next) {
    try {
      const { name, link, projectId } = req.body;
      const logoPath = req.file ? `/img/${req.file.filename}` : ''; // N·∫øu c√≥ ·∫£nh, l∆∞u ƒë∆∞·ªùng d·∫´n

      if (!name) {
        return res
          .status(400)
          .json({ message: 'T√™n ƒë·ªëi t√°c kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng' });
      }

      const project = await Project.findById(projectId);
      if (!project) {
        return res.status(400).json({ message: 'Project kh√¥ng t·ªìn t·∫°i!' });
      }

      // Th√™m ƒë·ªëi t√°c
      const partner = new Partner({
        name,
        logo: logoPath,
        link,
        project: projectId,
      });
      await partner.save();

      // Th√™m v√†o danh s√°ch ƒë·ªëi t√°c c·ªßa project
      project.partners.push(partner._id);
      await project.save();

      res.redirect('back');
    } catch (error) {
