const multer = require('multer');
const path = require('path');

// C·∫•u h√¨nh l∆∞u tr·ªØ cho ·∫£nh v√† t√†i li·ªáu
const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    const allowedImages = ['image/jpeg', 'image/png', 'image/jpg'];
    const allowedDocs = [
      'application/pdf',
      'application/msword',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    ];

    if (allowedImages.includes(file.mimetype)) {
      cb(null, 'src/public/img/'); // L∆∞u ·∫£nh v√†o th∆∞ m·ª•c img
    } else if (allowedDocs.includes(file.mimetype)) {
      cb(null, 'src/public/docs/'); // L∆∞u t√†i li·ªáu v√†o th∆∞ m·ª•c docs
    } else {
      cb(new Error('Ch·ªâ h·ªó tr·ª£ JPG, JPEG, PNG, PDF, DOCX!'), false);
    }
  },
  filename: function (req, file, cb) {
    // Gi·ªØ nguy√™n t√™n g·ªëc nh∆∞ng th√™m timestamp ƒë·ªÉ tr√°nh tr√πng l·∫∑p
    const name = path.parse(file.originalname).name;
    const ext = path.extname(file.originalname);
    const uniqueName = `${name}-${Date.now()}${ext}`;
    cb(null, uniqueName);
  },
});

// B·ªô l·ªçc lo·∫°i file
const upload = multer({
  storage: storage,
  fileFilter: (req, file, cb) => {
    const allowedTypes = [
      'image/jpeg', 'image/png', 'image/jpg',
      'application/pdf', 'application/msword',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    ];

    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Ch·ªâ h·ªó tr·ª£ JPG, JPEG, PNG, PDF, DOCX!'), false);
    }
  },
});

module.exports = upload;

