const Events = require('../models/Events');
const Project = require('../models/Project');
const Hopphan = require('../models/Hopphan');
const Tags = require('../models/Tags');

const { mutipleMongooseToObject } = require('../../util/mongoose');
const fs = require('fs');
const path = require('path');

class EventsController {
  create(req, res, next) {
    // res.render('admin/events/create');
    Promise.all([
      Tags.find({}).lean(),
      Hopphan.find({}).lean(),
    ])
    .then(([tags, hopphan]) => res.render('admin/events/create', { tags, hopphan }))
    .catch(next);
  }
  // [POST]/khoa/store
  store(req, res, next) {
    try {
      // ·∫¢nh ch√≠nh
      const imagePath = req.files['image']
        ? `/img/${req.files['image'][0].filename}`
        : '';

      // ·∫¢nh ph·ª•
      // const imagesPaths = req.files['images']
      //   ? req.files['images'].map((file) => `/img/${file.filename}`)
      //   : [];
      const imagesPaths = req.files['images']
        ? req.files['images'].map((file) => ({ path: `/img/${file.filename}`, approve: 0 ,isNotified: false }))
        : [];

      const documentPaths = req.files['documents']
      ? req.files['documents'].map((file) => ({
          path: `/docs/${file.filename}`,
          originalName: file.originalname,
          approve: 0,
          isNotified: false
        }))
      : [];

      const eventsData = {
        name: req.body.name,
        overview: req.body.overview,
        aim: req.body.aim,
        activities: req.body.activities,
        outcomes: req.body.outcomes,
        url: req.body.url,
        dateup: req.body.dateup ? new Date(req.body.dateup) : Date.now,
        image: imagePath,
        images: imagesPaths,
        documents: documentPaths,
        tags: Array.isArray(req.body.tags) ? req.body.tags : [req.body.tags],
        hopphan: Array.isArray(req.body.hopphan) ? req.body.hopphan : [req.body.hopphan]
      };

      const events = new Events(eventsData);
      events
        .save()
        .then(() => res.redirect('/events/view'))
        .catch((error) => next(error));
    } catch (error) {
      next(error);
    }
  }
  view(req, res, next) {
    Events.find({})
      .then((event) => {
        // res.json(event);
        res.render('admin/events/view', {
          event: mutipleMongooseToObject(event),
        });
      })

      .catch(next);
  }

  // detail(req, res, next) {
  //   Events.findOne({ slug: req.params.slug })
  //     .lean()
  //     .then((event) => res.render('admin/events/detail', { event }))
  //     .catch(next);

  // }
  // detail(req, res, next) {
  //   Events.findOne({ slug: req.params.slug })
  //     .populate('tags')
  //     .populate('hopphan')  
  //     .lean()
  //     .then((event) => res.render('admin/events/detail', { event }))
  //     .catch(next);
  // }

  detail(req, res, next) {
    Events.findOne({ slug: req.params.slug })
      .populate('tags')
      .populate('hopphan')
      .lean()
      .then((event) => {
        if (event) {
          // L·ªçc ch·ªâ nh·ªØng ·∫£nh v√† t√†i li·ªáu ƒë√£ duy·ªát
          event.images = event.images.filter((img) => img.approve === 1);
          event.documents = event.documents.filter((doc) => doc.approve === 1);
        }

        res.render('admin/events/detail', { event });
      })
      .catch(next);
  }

  edit(req, res, next) {
    Promise.all([
      Events.findById(req.params.id).lean(),
      Tags.find({}).lean(),
      Hopphan.find({}).lean(),
    ])
      .then(([event, tags, hopphan]) => {
        res.render('admin/events/edit', { event, tags, hopphan });
      })
      .catch(next);
    // Events.findById(req.params.id)
    //   .lean()
    //   .then((event) => res.render('admin/events/edit', { event }))
    //   .catch(next);
  }

  update(req, res, next) {
    const updateData = {
      name: req.body.name,
      overview: req.body.overview,
      aim: req.body.aim,
      activities: req.body.activities,
      outcomes: req.body.outcomes,
      url: req.body.url,
      dateup: req.body.dateup ? new Date(req.body.dateup) : undefined,
      tags: Array.isArray(req.body.tags) ? req.body.tags.flat() : [req.body.tags],
      hopphan: Array.isArray(req.body.hopphan) ? req.body.hopphan.flat() : [req.body.hopphan]
    };

    // N·∫øu c√≥ ·∫£nh ch√≠nh m·ªõi
    if (req.files['image']) {
      updateData.image = `/img/${req.files['image'][0].filename}`;
    }

    // N·∫øu c√≥ th√™m ·∫£nh ph·ª• m·ªõi
    if (req.files['images']) {
      updateData.$push = {
        images: {
          $each: req.files['images'].map((file) => ({
            path: `/img/${file.filename}`,
            approve: 0,
            isNotified: false
          })),
        },
      };

    }

     // Th√™m t√†i li·ªáu m·ªõi
  if (req.files['documents']) {
    if (!updateData.$push) updateData.$push = {};
    updateData.$push.documents = {
      $each: req.files['documents'].map((file) => ({
        path: `/docs/${file.filename}`,
        originalName: file.originalname,
        approve: 0,
        isNotified: false,
      })),
    };
  }

  // C·∫≠p nh·∫≠t tr·∫°ng th√°i duy·ªát cho t√†i li·ªáu
  Events.findById(req.params.id)
    .then((event) => {
      if (event && event.documents) {
        event.documents.forEach((doc) => {
          doc.approve = req.body.approveDocs && req.body.approveDocs.includes(doc.path) ? 1 : 0;
        });
        return event.save();
      }
    })
    .then(() => res.redirect('back'))
    .catch((error) => next(error));


    Events.updateOne({ _id: req.params.id }, updateData)
      .then(() => res.redirect('back'))
      .catch((error) => next(error));
  }

  markAsSeen(req, res) {
    const { id } = req.params;

    Events.updateOne(
      { _id: id, 'images.isNotified': false },
      { $set: { 'images.$[].isNotified': true } }
    )
      .then(() => res.status(200).json({ message: '‚úÖ ƒê√£ ƒë√°nh d·∫•u ƒë√£ xem!' }))
      .catch((err) => res.status(500).json({ message: '‚ùå L·ªói khi c·∫≠p nh·∫≠t!' }));
  }

  updateApproveStatus(req, res) {
   const { id, imageName } = req.params;
    const { approve } = req.body;

    Events.findOne({ _id: id, 'images.path': `/img/${imageName}` })
      .then((event) => {
        if (!event) {
          return res.status(404).json({ message: '‚ùå ·∫¢nh kh√¥ng t·ªìn t·∫°i!' });
        }

        // T√¨m ·∫£nh c·ª• th·ªÉ trong m·∫£ng
        const image = event.images.find(img => img.path === `/img/${imageName}`);

        // N·∫øu tr·∫°ng th√°i kh√¥ng thay ƒë·ªïi, b·ªè qua c·∫≠p nh·∫≠t
        if (image.approve === approve) {
          return res.status(200).json({ message: '‚ö†Ô∏è Tr·∫°ng th√°i duy·ªát kh√¥ng thay ƒë·ªïi!' });
        }

        // C·∫≠p nh·∫≠t tr·∫°ng th√°i n·∫øu c√≥ thay ƒë·ªïi
        return Events.updateOne(
          { _id: id, 'images.path': `/img/${imageName}` },
          { $set: { 'images.$.approve': approve } }
        ).then(() => {
          res.status(200).json({ message: '‚úÖ C·∫≠p nh·∫≠t tr·∫°ng th√°i duy·ªát th√†nh c√¥ng!' });
        });
      })
      .catch((err) => res.status(500).json({ message: '‚ùå L·ªói khi c·∫≠p nh·∫≠t!' }));
  }

  // X√≥a ·∫£nh ph·ª• kh·ªèi s·ª± ki·ªán
  deleteSubImage(req, res, next) {
    const { id, imageName } = req.params;

    if (!imageName) {
      return res.status(400).json({ message: 'T√™n ·∫£nh kh√¥ng h·ª£p l·ªá!' });
    }

    const decodedImageName = decodeURIComponent(imageName);
    const imagePath = path.join(
      __dirname,
      '../../public/img',
      decodedImageName,
    );

    // X√≥a ·∫£nh kh·ªèi th∆∞ m·ª•c
    fs.unlink(imagePath, (err) => {
      if (err && err.code !== 'ENOENT') {
        console.error('‚ùå L·ªói khi x√≥a ·∫£nh:', err);
        return res.status(500).json({ message: 'L·ªói khi x√≥a ·∫£nh!' });
      }

      // X√≥a ·∫£nh kh·ªèi m·∫£ng `images` trong MongoDB
      Events.updateOne(
        { _id: id },
        { $pull: { images: { path: `/img/${decodedImageName}` } } },
      )      

        .then(() => {
          res.status(200).json({ message: '‚úÖ X√≥a ·∫£nh th√†nh c√¥ng!' });
        })
        .catch((error) => {
          console.error('‚ùå L·ªói khi c·∫≠p nh·∫≠t MongoDB:', error);
          res.status(500).json({ message: 'L·ªói khi c·∫≠p nh·∫≠t d·ªØ li·ªáu!' });
        });
    });
  }

  // Duy·ªát t√†i li·ªáu
updateApproveDocStatus(req, res) {
  const { id, docName } = req.params;
  const { approve } = req.body;

  Events.updateOne(
    { _id: id, 'documents.path': `/docs/${docName}` },
    { $set: { 'documents.$.approve': approve } }
  )
  .then(() => res.status(200).json({ message: '‚úÖ Duy·ªát t√†i li·ªáu th√†nh c√¥ng!' }))
  .catch(() => res.status(500).json({ message: '‚ùå L·ªói khi duy·ªát t√†i li·ªáu!' }));
}

// X√≥a t√†i li·ªáu
deleteDocument(req, res) {
  const { id, docName } = req.params;

  Events.updateOne(
    { _id: id },
    { $pull: { documents: { path: `/docs/${docName}` } } }
  )
  .then(() => res.status(200).json({ message: '‚úÖ X√≥a t√†i li·ªáu th√†nh c√¥ng!' }))
  .catch(() => res.status(500).json({ message: '‚ùå L·ªói khi x√≥a t√†i li·ªáu!' }));
}

viewDocument(req, res, next) {
  const { docName } = req.params;
  const filePath = path.join(__dirname, `../../public/docs/${docName}`);

  fs.access(filePath, fs.constants.F_OK, (err) => {
    if (err) {
      return res.status(404).send('‚ùå T√†i li·ªáu kh√¥ng t·ªìn t·∫°i!');
    }

    // N·∫øu l√† PDF th√¨ render tr·ª±c ti·∫øp
    if (filePath.endsWith('.pdf')) {
      res.sendFile(filePath);
    } 
    // N·∫øu l√† DOC/DOCX th√¨ g·ª£i √Ω t·∫£i v·ªÅ ho·∫∑c chuy·ªÉn sang PDF n·∫øu mu·ªën n√¢ng c·∫•p
    else if (filePath.endsWith('.doc') || filePath.endsWith('.docx')) {
      res.download(filePath, docName);
    } else {
      res.status(400).send('‚ùå ƒê·ªãnh d·∫°ng t√†i li·ªáu kh√¥ng ƒë∆∞·ª£c h·ªó tr·ª£!');
    }
  });
}

downloadDocument(req, res, next) {
  const { id, docName } = req.params;

  Events.findOne({ _id: id, 'documents.path': `/docs/${docName}` })
    .then((event) => {
      if (!event) {
        return res.status(404).send('‚ùå T√†i li·ªáu kh√¥ng t·ªìn t·∫°i!');
      }

      const document = event.documents.find(doc => doc.path === `/docs/${docName}`);
      if (!document) {
        return res.status(404).send('‚ùå T√†i li·ªáu kh√¥ng t·ªìn t·∫°i!');
      }

      const filePath = path.join(__dirname, `../../public${document.path}`);
      const originalName = document.originalName || docName; // ∆Øu ti√™n t√™n g·ªëc n·∫øu c√≥

      fs.access(filePath, fs.constants.F_OK, (err) => {
        if (err) {
          return res.status(404).send('‚ùå File kh√¥ng t·ªìn t·∫°i tr√™n server!');
        }

        // T·∫£i v·ªÅ v·ªõi t√™n g·ªëc
        res.download(filePath, originalName, (err) => {
          if (err) {
            console.error('‚ùå L·ªói khi t·∫£i t√†i li·ªáu:', err);
            return res.status(500).send('‚ùå L·ªói khi t·∫£i t√†i li·ªáu!');
          }
        });
      });
    })
    .catch((err) => {
      console.error('‚ùå L·ªói:', err);
      next(err);
    });
}



  // [DELETE]/khoa/:id
  destroy(req, res, next) {
    // res.json(req.body)
    Events.delete({ _id: req.params.id })
      .then(() => res.redirect('back'))
      .catch(next);
  }

  trash(req, res, next) {
    Events.findDeleted({})
      .then((event) => {
        res.render('admin/events/trash', {
          event: mutipleMongooseToObject(event),
        });
      })

      .catch(next);
  }

  force(req, res, next) {
    // res.json(req.body)
    Events.deleteOne({ _id: req.params.id })
      .then(() => res.redirect('back'))
      .catch(next);
  }
