const Tags = require('../models/Tags');
const { mutipleMongooseToObject } = require('../../util/mongoose');

class TagController {

  // Hi·ªÉn th·ªã form t·∫°o m·ªõi tag
  create(req, res, next) {
    res.render('admin/tag/create');
  }

  // L∆∞u tag v√†o database
  store(req, res, next) {
    const tag = new Tags(req.body);
    tag
      .save()
      .then(() => res.redirect('back'))
      .catch((error) => {
        console.error('L·ªói khi th√™m tag:', error);
        res.status(500).json({ message: 'L·ªói khi th√™m tag!' });
      });
  }

  // Hi·ªÉn th·ªã t·∫•t c·∫£ c√°c tag
  view(req, res, next) {
    Tags.find({})
      .then((tags) => {
        res.render('admin/tag/view', { tags: mutipleMongooseToObject(tags) });
      })
      .catch(next);
  }

  // Hi·ªÉn th·ªã form ch·ªânh s·ª≠a tag
  edit(req, res, next) {
    Tags.findById(req.params.id)
      .lean()
      .then((tag) => res.render('admin/tag/edit', { tag }))
      .catch(next);
  }

  // C·∫≠p nh·∫≠t tag
  update(req, res, next) {
    Tags.updateOne({ _id: req.params.id }, req.body)
      .then(() => res.redirect('/tags/view'))
      .catch(next);
  }

  // X√≥a m·ªÅm tag
  destroy(req, res, next) {
    Tags.delete({ _id: req.params.id })
      .then(() => res.redirect('back'))
      .catch(next);
  }

  // Hi·ªÉn th·ªã c√°c tag ƒë√£ x√≥a
  trash(req, res, next) {
    Tags.findDeleted({})
      .then((tags) => res.render('admin/tag/trash', { tags: mutipleMongooseToObject(tags) }))
      .catch(next);
  }

  // Kh√¥i ph·ª•c tag ƒë√£ x√≥a
  restore(req, res, next) {
    Tags.restore({ _id: req.params.id })
      .then(() => res.redirect('back'))
      .catch(next);
  }

  // X√≥a vƒ©nh vi·ªÖn
  force(req, res, next) {
    Tags.deleteOne({ _id: req.params.id })
      .then(() => res.redirect('back'))
      .catch(next);
  }
}

module.exports = new TagController();

